/* UTF-8: SÍ  Con estas vistas se generan las exportaciones para procesamiento:  */drop view encu.vista_ajus_ajh1_  ;drop view encu.vista_ajus_ajh1_m ;drop view encu.vista_ajus_aji1_  ;create or replace view encu.vista_ajus_ajh1_  as select pla_replica, pla_comuna, pla_up_comuna as pla_up, case pla_replica when 7 then 'v' when 8 then 'i' else 'c' end as pla_dominio, pla_polifiscalias_zona, t.pla_bolsa, p.* from encu.plana_ajh1_  p inner join encu.plana_tem_ t on t.pla_enc=p.pla_enc and t.pla_hog=0 and t.pla_mie=0;create or replace view encu.vista_ajus_ajh1_m as select pla_replica, pla_comuna, pla_up_comuna as pla_up, case pla_replica when 7 then 'v' when 8 then 'i' else 'c' end as pla_dominio, pla_polifiscalias_zona, t.pla_bolsa, p.* from encu.plana_ajh1_m p inner join encu.plana_tem_ t on t.pla_enc=p.pla_enc and t.pla_hog=0 and t.pla_mie=0;create or replace view encu.vista_ajus_aji1_  as select pla_replica, pla_comuna, pla_up_comuna as pla_up, case pla_replica when 7 then 'v' when 8 then 'i' else 'c' end as pla_dominio, pla_polifiscalias_zona, t.pla_bolsa, p.* from encu.plana_aji1_  p inner join encu.plana_tem_ t on t.pla_enc=p.pla_enc and t.pla_hog=0 and t.pla_mie=0;ALTER TABLE encu.vista_ajus_ajh1_  OWNER TO tedede_php;ALTER TABLE encu.vista_ajus_ajh1_m OWNER TO tedede_php;ALTER TABLE encu.vista_ajus_aji1_  OWNER TO tedede_php;GRANT SELECT ON TABLE encu.vista_ajus_ajh1_  TO yeah_solo_lectura_formularios;GRANT SELECT ON TABLE encu.vista_ajus_ajh1_m TO yeah_solo_lectura_formularios;GRANT SELECT ON TABLE encu.vista_ajus_aji1_  TO yeah_solo_lectura_formularios;