/*GRANT CREATE ON DATABASE cvp_db TO cvpowner;*/-- CREATE SCHEMA ipcba AUTHORIZATION cvpowner;alter role cvpowner set search_path = ipcba,cvp,comun,public;CREATE OR REPLACE FUNCTION comun.probar(p_sentencia text)  RETURNS text AS$BODY$begin  execute p_sentencia;  return 'Ejecuto sin excepciones';exception  when others then    return sqlstate || ': ' || sqlerrm;end;  $BODY$  LANGUAGE plpgsql VOLATILE  COST 100;ALTER FUNCTION comun.probar(text)  OWNER TO cvpowner;GRANT USAGE ON SCHEMA ipcba TO cvpowner;-- Table: his.modificaciones-- DROP TABLE his.modificaciones;CREATE TABLE his.modificaciones(  mdf_mdf serial NOT NULL,  mdf_tabla character varying(50) NOT NULL,  mdf_operacion character varying(1) NOT NULL,  mdf_pk character varying(2000) NOT NULL,  mdf_campo character varying(2000) NOT NULL,  mdf_actual text,  mdf_anterior text,  mdf_tlg bigint,  CONSTRAINT modificaciones_pkey PRIMARY KEY (mdf_mdf ))WITH (  OIDS=FALSE);ALTER TABLE his.modificaciones  OWNER TO cvpowner;  -- luego en el navegador: http://localhost/yeah/ipcba/ipcba.php?hacer=instalar&sin_login=1