SET search_path = encu, pg_catalog;

CREATE TABLE actividades_codigos (
    codigo integer NOT NULL,
    nombre_variable text,
    texto text,
    abr text,
    detalle text,
    imagen text,
    exclusividad boolean DEFAULT false,
    grupo character varying(1),
    obligatoriedad boolean DEFAULT false,
    opcion_d12 boolean,
    opcion_d13 boolean,
    opcion_d22 boolean,
    opcion_d23 boolean,
    act_cod_tlg bigint NOT NULL
);

ALTER TABLE actividades_codigos OWNER TO tedede_php;




ALTER TABLE ONLY actividades_codigos
    ADD CONSTRAINT act_cod_pkey PRIMARY KEY (codigo);
    
ALTER TABLE actividades_codigos
   ADD CONSTRAINT "Grupos considerados" CHECK (grupo::text in ('D','F','M'));    

ALTER TABLE ONLY actividades_codigos
    ADD CONSTRAINT actividades_codigos_tiempo_logico_fk FOREIGN KEY (act_cod_tlg) REFERENCES tiempo_logico(tlg_tlg);


GRANT ALL ON TABLE actividades_codigos TO tedede_php;
GRANT SELECT ON TABLE actividades_codigos TO ut2016_ro;


